?>
  <?php $featured = nesia_get_post_by_meta('featured', $nesia_options['nesia_featured_count']); ?>
  <?php if($featured->have_posts()): ?>
  <section class='featured-post-wrapper section-container post-carousel featured-post-carousel'>
    <div class='section-title'>
      <h4><?php _e('Featured Articles', 'nesia'); ?></h4>
    </div>
    <div class='featured-post row'>
      <?php while($featured->have_posts()): $featured->the_post(); ?>
      <article class='entry-post span4'>
        <div class='innercontainer'>
          <figure class='entry-top'>
            <a href='<?php the_permalink(); ?>'>
              <?php nesia_image('width=329&height=222&force=true'); ?>
            </a>
            <span class="entry-comment-count"><i class="icon-comments"></i> <?php echo get_comments_number( get_the_ID() ); ?></span>
            <span class="entry-likes"><i class="icon-heart"></i> <?php echo nesia_get_count_voted( get_the_ID() ); ?></span>
          </figure>
          <div class='entry-category'>
            <?php the_category(', '); ?>
          </div>
          <header class='entry-header'>
            <h2><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>
          </header>
          <time class='entry-date'><?php echo get_the_date(); ?></time>
        </div>
      </article>
      <?php endwhile; ?>
    </div>
  </section>
  <!-- /End of Featured Slider -->
  <?php endif; wp_reset_postdata(); ?>