/*-----------------------------------------------------------------------------------*/
/* Nesia Framework Version & Theme Version */
/*-----------------------------------------------------------------------------------*/
function nesia_version_init() {

    $nesia_framework_version = '1.0.3';

    if ( get_option( 'nesia_framework_version' ) != $nesia_framework_version ) {
    	update_option( 'nesia_framework_version', $nesia_framework_version );
    }

}
add_action( 'init', 'nesia_version_init', 10 );

function nesia_version(){

    if(function_exists('wp_get_theme'))
            $theme_data = wp_get_theme();
        else
            $theme_data = get_theme_data( get_template_directory() . '/style.css' );
        
    $theme_version = $theme_data['Version'];
    $nesia_framework_version = get_option( 'nesia_framework_version' );

	echo "\n<!-- Theme version -->\n";
    echo '<meta name="generator" content="'. esc_attr( get_option( 'nesia_themename' ) ) . ' ' . $theme_version . '" />' ."\n";
    echo '<meta name="generator" content="NesiaFramework '. esc_attr( $nesia_framework_version ) .'" />' ."\n";

}
add_action( 'wp_head', 'nesia_version', 10 );

