 */
global $nesia_options, $wp_locale;
get_header();
/**
 * get monthly and yearly number
 */
$monthly        = (isset($_GET['monthly']) && $_GET['monthly'])? $_GET['monthly']:false;
$yearly         = (isset($_GET['yearly']) && $_GET['yearly'])? $_GET['yearly']:false;
?> 
<div class='main-container container'>
        <?php the_post(); ?>
      <div class='row'>
        <section class='main span12'>
          <div class='page-title'>
            <h4><?php the_title(); ?></h4>
            <select class='articles-filter' onchange="document.location.href=this.options[this.selectedIndex].value;">
              <option><?php _e('Select month', 'nesia'); ?></option>
              <?php echo nesia_get_monthly_date(); ?>
            </select>
          </div>
          
          <div class='archive-timeline-wrapper'>
            <div class='timeline-header'>
              <?php $text_current   = ($monthly && $yearly)? sprintf( __('%s %s', 'nesia'), $wp_locale->get_month($monthly), $yearly):__('Current Month', 'nesia'); ?>
              <h3><?php echo $text_current; ?></h3>
            </div>
            <?php 
                // Fix for the WordPress 3.0 "paged" bug.
                $paged = 1;
                if ( get_query_var( 'paged' ) ) { $paged = get_query_var( 'paged' ); }
                if ( get_query_var( 'page' ) ) { $paged = get_query_var( 'page' ); }
                $paged = intval( $paged );
            ?>
            <?php $query_args   = ($monthly && $yearly)? "&monthnum=$monthly&year=$yearly":''; ?>
            <?php query_posts("post_type=post$query_args&paged=$paged&posts_per_page=" . get_option('posts_per_page', 5)); ?>
            <?php if(have_posts()): ?>
            <div class='timeline-post row'>
              <?php while( have_posts() ): the_post(); ?>
              <article class='entry-post span6'>
                <div class='innercontainer'>
                  <?php get_template_part('content', 'entry-info'); ?>
                  
                    <figure class='entry-top'>
                      <a href='<?php the_permalink(); ?>'>
                        <?php nesia_image('width=568&height=382&force=true'); ?>
                      </a>
                    </figure>
                  <div class='entry-container'>
                    <div class='entry-category'>
                      <?php the_category(', '); ?>
                    </div>
                    <header class='entry-header'>
                      <h2><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>
                    </header>
                    <time class='entry-date'><?php echo get_the_date(); ?></time>
                    <div class='entry-content'>
                      <?php the_excerpt(); ?>
                    </div>
                  </div>
                  <a class='entry-more-link' href='<?php the_permalink(); ?>'><?php _e('Read This Article', 'nesia'); ?></a>
                  <div class='time-indicator' rel='tooltip' title='<?php echo get_the_date( __('F tS, Y \a\t h:i:s A', 'nesia') ); ?>'></div>
                </div>
              </article>
              <?php endwhile; ?>
            </div>
            <?php 
              // pagination
              get_template_part('content', 'pagination');
            ?>
